  //         <thead>
  //           <tr>
  //             <td>Title</td>
  //             <td>Price</td>
  //             <td>Count</td>
  //           </tr>
  //         </thead>
  //         {/* 2 Loop Table */}
  //         {item.products.map((p, i) => (
  //           <tr>
  //             <td>{p.product.title}</td>
  //             <td>{p.price}</td>
  //             <td>{p.count}</td>
  //           </tr>
  //         ))}
  //         <tr>
  //           <td colSpan={3}>
  //             ราคาสุทธิ:{" "}
  //             <b>
  //               <u>{item.cartTotal}</u>
  //             </b>
  //           </td>
  //         </tr>
  //       </table>
  //     </div>
  //   );
  // });

  const columns = [
    {
      title: "username",
      dataIndex: "orderdBy",
      render: (item, i) => <>{item.username}</>,
    },
    {
      title: "product",
      render: (item, i) => (
        <ol>
    
          {item.products.map((p, i) => (
             
            <li>
              {p.product.title}
            </li>
           
          ))}
    
    </ol>
      ),
    },
    
    {
      title: "quantity",
      render: (item, i) => (
        <>
          {item.products.map((p, i) => (
          
           
              <li>
                {p.count} ชิ้น
              </li>
           
          ))}
        </>
      ),
    },
    {
      title: "total",
      dataIndex: "cartTotal",
      key: "cartTotal",
      render: (item, i) => (
        <>
              <li>
                {item} บาท
              </li>

        </>
      ),
    },
    {
      title: "ที่อยู่ผู้รับ",
      dataIndex: "orderdBy",
      render: (item, i) => <>{item.address}</>,
    },
    {
      title: "สถานะ",
      dataIndex: "orderstatus",
      key: "orderstatus",
    },
  
    {
      title: "อัพเดทสถานะ",
      render: (item) => (
        <select
          value={item.orderstatus}
          onChange={(e) => handleChangeStatus(item._id, e.target.value)}
          style={{ width: "200px", alignSelf: "center" }}
          className="form form-control"
        >
          <option value="Not Process">Not Process</option>
          <option value="Processing">Processing</option>
          <option value="Cancelled">Cancelled</option>
          <option value="Completed">Completed</option>
        </select>
      ),
    },
  ];

  // const tableBoot = (
