import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import ProductCart from "../card/ProductCard";

//function
import { listProduct } from "../functions/product";


const Shop = () => {
    const [product, setProduct] = useState([]);
    const [loading, setLoading] = useState(false);


    useEffect(() => {
        //code
        loadData();
    }, []);


    const loadData = () => {
        setLoading(true);
        //code
        listProduct(2)
            .then((res) => {
                setProduct(res.data);
                setLoading(false);
            }).catch((err) => {
                setLoading(false);
                console.log(err);
            });
    };


    return (
        <>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-3">
              Filter / Search
              <hr />
              <h4>ค้นหาด้วยราคาสินค้า</h4>
              <Slider value={price} onChange={handlePrice} range max={100000} />
              <hr />
              <h4>ค้นหาตามหมวดหมู่สินค้า</h4>
              {category.map((item, index) => (
                <Checkbox onChange={handleCheck} value={item._id}>
                  {item.name}
                </Checkbox>
              ))}
            </div>
  
            <div className="col-md-9">
              {loading ? (
                <h4 className="text-danger">Loading...</h4>
              ) : (
                <h4 className="text-info">Product</h4>
              )}
  
              {product.length < 1 && <p>No Product found</p>}
  
              <div className="row pb-5">
                {product.map((item, index) => (
                  <div key={index} className="col-md-4 mt-3">
                    <ProductCart product={item} />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </>
    )
}

export default Shop
