import React,{ useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'

const LoadingToRedirect = () => {

    const [ count, setCount ] = useState(3);// เก็บค่านับเลข ถ้าไม่สิทธิ์เข้า page จะ redirect ไปหน้าแรก
    const navigator = useNavigate()

    useEffect(() => {// เรียกใช้งานฟังก์ชัน useEffect เพื่อทำงานตามที่กำหนด
        const interval = setInterval(() => {// ทำงานทุกวินาที 
            setCount(currentCount => currentCount - 1)// ลดค่า count ลงไป
            }, 1000)
        count === 0 && navigator('/')// ถ้าค่า count เท่ากับ 0 และเรียกใช้งานฟังก์ชัน navigate เพื่อไปหน้าแรก
        return () => clearInterval(interval)
    },[count])


  return (
    <div>
        <h1>No Permission, redirect in {count}</h1>
    </div>
  )
}

export default LoadingToRedirect
