import React, { useState } from 'react'
import { login } from '../../functions/auth';// function 
//redux
import { useDispatch } from 'react-redux';// useDispatch เรียกใช้งานฟังก์ชันจาก redux

// ไปแต่ละหน้า
import { useNavigate } from 'react-router-dom';




const Login = () => {
    const dispatch = useDispatch();// เรียกใช้งานฟังก์ชันจาก redux
    const navigate = useNavigate();// เรียกใช้งานฟังก์ชันจาก react-router-dom เพื่อไปหน้าอื่นๆ
    
    const [value, setValue] = useState({// เก็บค่าที่รับมาจาก input
        username: "",
        password: "",
    });

    const roleBaseRedirect = (role) => {// สำหรับการ redirect 
        if (role === 'admin') {
            navigate('/admin/index');
        } else if (role === 'user') {
            navigate('/user/index');
        }
    }

    const handleChange = (e) => {
        setValue({ ...value, [e.target.name]: e.target.value });// เก็บค่าที่รับมาจาก input ไว้ในตัวแปร value
    };

    



    // ฟังก์ชันสำหรับการส่งข้อมูล
    const handleSubmit = (e) => {
        e.preventDefault();// ไม่ให้หน้าเว็บ refresh
        console.log(value);
       
            login(value)
                .then((res) => {// ถ้าส่งข้อมูลไปหลังบ้านสำเร็จ
                    console.log(res.data);
                    alert(res.data);
                    dispatch({
                         type: "LOGIN", 
                         payload: {// เรียกใช้ฟังก์ชันจาก redux เพื่อเก็บค่าที่ได้จากการ login ไว้ใน store
                                token: res.data.token,
                                user: res.data.payload.user.username,
                                role: res.data.payload.user.role,
                                mobile: res.data.payload.user.mobile,
                                fullname: res.data.payload.user.fullname,
                                details: res.data.payload.user.details,
                         }
                        });
                        //อยู่ใน Application localStorage ทำให้ไม่หายจนกว่า token จะหมดอายุ
                        localStorage.setItem('token', res.data.token);// เก็บค่า token ไว้ใน localStorage
                        roleBaseRedirect(res.data.payload.user.role);// เรียกใช้ฟังก์ชันสำหรับ redirect

                }).catch((err) => {// ถ้าส่งข้อมูลไปหลังบ้านไม่สำเร็จ
                    console.log(err.response.data);
                    alert(err.response.data);
                });
        
    };





    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <label>Username</label>
                <input type="text" name='username' onChange={handleChange} /> {/*เมื่อเปลี่ยนค่าให้ทำการเรียกใช้ handleChange*/}

                <label>Password</label>
                <input type="text" name='password' onChange={handleChange} />


                <button  type='submit'>Login</button> 

            </form>
        </div>
    )
}

export default Login
