// rafce
import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";


import QRCode from 'qrcode.react';

// function
import {
  getUserCart,
  saveAddress,
  saveOrder,
  emptyCart,
} from "../functions/users";
import { useNavigate} from 'react-router-dom'

import ReactQuill from "react-quill"; // ES6
import "react-quill/dist/quill.snow.css"; // ES6
import { toast } from "react-toastify";

const CheckOut = () => {
  const generatePayload = require('promptpay-qr');

  const phoneNumber = ("0889249523");
  const [ qrCode ,setqrCode ] = useState("sample");
  const { user } = useSelector((state) => ({ ...state }));
  const dispatch = useDispatch();
  const navigate = useNavigate()




  const [products, setProducts] = useState([]);
  const [total, setTotal] = useState(0);
  const [address, setAddress] = useState("");
  const [addressSaved, setAddressSaved] = useState(false);
  useEffect(() => {
    getUserCart(user.token).then((res) => {
      console.log(res.data);
      setProducts(res.data.products);
      setTotal(res.data.cartTotal);
    });
  }, []);

  const handleSaveAddress = () => {
    console.log(address);
    saveAddress(user.token, address).then((res) => {
      console.log(res.data);
      if (res.data.ok) {
        toast.success("Address Saved");
        setAddressSaved(true);
      }
    });
  };

  const handleCreateOrder = () => {
    // code
    saveOrder(user.token).then((res) => {
      console.log(res.data);
      // clear DB
      emptyCart(user.token);
      // clear store
      dispatch({
        type: "ADD_TO_CART",
        payload: [],
      });
      // local localStorage
      if (typeof window !== "undefined") {
        localStorage.removeItem("cart");
      }

      toast.success("Save Order Success");
      navigate('/user/history')
    });
  };

 function handleQR() {
    setqrCode(generatePayload(phoneNumber, { amount: total+35 }));
  }


  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-6">
          <h4>Address</h4>
          <br />
          ข้อมูลที่อยู่สำหรับจัดส่ง พร้อมเบอร์โทรศัพท์
          <textarea class="form-control w-100 "
          type="text" 
          placeholder="ที่อยู่สำหรับจัดส่งและเบอร์โทรศัพท์"
          value={address}
          
          onChange={(e) => setAddress(e.target.value)}
        />
            {/* <ReactQuill value={address} onChange={setAddress} /> */}
          <button className="btn btn-primary m-2 " onClick={handleSaveAddress}>
            Save Address
          </button>
        </div>

        <div className="col-md-6">
          <h4>Order Summary </h4>
          <hr />
          <p>
            Product <b>{products.length}</b>
          </p>
          <hr />
          <p>List of product</p>
          {products.map((item, i) => (
            <div key={i}>
              <p>
                {item.product.title} x {item.count} = {item.price * item.count}
                Delivery : {item.product.title}
              </p>
            </div>
          ))}
          <hr />
          Total : <b>{total}</b>
          <br />
          <button
            onClick={handleCreateOrder}
            disabled={!addressSaved || !products.length}
            className="btn btn-primary mt-3"
          >
            Check Out
          </button>
          <div>
 

      <button onClick={handleQR}>Generate Promptpay QR</button>
      <QRCode value={qrCode} />
    </div>
        </div>
      </div>
    </div>
  );
};

export default CheckOut;
