import React, { useState } from 'react'
import { login } from '../../functions/auth';


const Login = () => {
    const [value, setValue] = useState({// เก็บค่าที่รับมาจาก input
        username: "",
        password: "",
     
    });

    const handleChange = (e) => {
        setValue({ ...value, [e.target.name]: e.target.value });// เก็บค่าที่รับมาจาก input ไว้ในตัวแปร value
    };

    



    // ฟังก์ชันสำหรับการส่งข้อมูล
    const handleSubmit = (e) => {
        e.preventDefault();// ไม่ให้หน้าเว็บ refresh
        console.log(value);
       
            login(value)
                .then((res) => {// ถ้าส่งข้อมูลไปหลังบ้านสำเร็จ
                    console.log(res.data);
                    alert("Register Success");
                }).catch((err) => {// ถ้าส่งข้อมูลไปหลังบ้านไม่สำเร็จ
                    console.log(err.response.data);
                    alert(err.response.data);
                });
        
    };





    return (
        <div>
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <label>Username</label>
                <input type="text" name='username' onChange={handleChange} /> {/*เมื่อเปลี่ยนค่าให้ทำการเรียกใช้ handleChange*/}

                <label>Password</label>
                <input type="text" name='password' onChange={handleChange} />


                <button disabled={value.password.length < 6} type='submit'>Register</button> {/*ถ้า password น้อยกว่า 6 ตัว ปุ่มจะไม่สามารถกดได้*/}

            </form>
        </div>
    )
}

export default Login
