import React, { useState , useEffect} from "react"// useState การเก็บค่า useEffect ทำฉันด้วย
import Register from "./components/pages/auth/Register";
import { Routes, Route } from "react-router-dom";
import Login from "./components/pages/auth/Login";
import Home from "./components/pages/Home";

// Layout
import Navbar from "./components/layouts/Navbar";

// pages admin
import HomeAdmin from "./components/pages/admin/Home";

// pages user
import HomeUser from "./components/pages/user/Home";





function App() {
  const idtoken = localStorage.getItem("idtoken");// เรียกใช้งานฟังก์ชันจาก localStorage เพื่อเก็บค่าที่ได้จากการ login ไว้ในตัวแปร idtoken
  if(idtoken){
    console.log(idtoken);
  }



  return (
    <div className="App">
      <Navbar/>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        <Route path="/admin/index" element={<HomeAdmin />} />
        <Route path="/user/index" element={<HomeUser />} />

      </Routes>

    </div>
  );
}

export default App;
