import React, { useState, useEffect } from "react"// useState การเก็บค่า useEffect ทำฉันด้วย
import Register from "./components/pages/auth/Register";
import { Routes, Route } from "react-router-dom";
import Login from "./components/pages/auth/Login";
import Home from "./components/pages/Home";

// Layout
import Navbar from "./components/layouts/Navbar";

// pages admin
import HomeAdmin from "./components/pages/admin/Home";

// pages user
import HomeUser from "./components/pages/user/Home";

// function
import { currentUser } from "./components/functions/auth";

// redux
import { useDispatch } from 'react-redux';// useDispatch เรียกใช้งานฟังก์ชันจาก

//Routes
import UserRoute from "./components/routes/UserRoute";
import AdminRoute from "./components/routes/AdminRoute";


function App() {

  const dispatch = useDispatch();
  const idtoken = localStorage.token;
  if (idtoken) {
    currentUser(idtoken)
      .then((res) => {
        //code 
        console.log(res.data);//ได้ข้อมูลมาแล้วส่งข้อมูลไปเก็บ
        dispatch({
          type: "LOGIN",
          payload: {// เรียกใช้ฟังก์ชันจาก redux เพื่อเก็บค่าที่ได้จากการ login ไว้ใน store
            token: idtoken,
            user: res.data.username,
            role: res.data.role,
            mobile: res.data.mobile,
            fullname: res.data.fullname,
            details: res.data.details,
          }//ค่าไปอยู่ที่ redux แล้ว
        });
      })
      .catch((err) => {
        //err
        console.log(err);
      });
  }






  return (
    <div className="App">
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        <Route path="/admin/index" element={<HomeAdmin />} />
        <Route
          path="/user/index"
          element={
            <UserRoute>
              <HomeUser />
            </UserRoute>
          } />


      </Routes>

    </div>
  );

}




export default App;
