import React, { useState, useEffect } from "react";
import MenubarAdmin from "../../layouts/MenubarAdmin";
import { useSelector } from "react-redux";

// function
import { listProduct, removeProduct } from "../../functions/product";
import AdminProductCard from "../../card/AdminProductCard";
// notify
import { toast } from "react-toastify";

const Home = () => {
  const { user } = useSelector((state) => ({ ...state }));
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    //
    loadData(100);
  }, []);

  const loadData = (count) => {
    setLoading(true);
    listProduct(count)
      .then((res) => {
        setLoading(false);
        setProduct(res.data);
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
      });
  };

  const handleRemove = (id) => {
    // console.log(id)
    if (window.confirm("Delete ?")) {
      removeProduct(user.token, id)
        .then((res) => {
          toast.success("Deleted "+res.data.title+" Success!!" )
          loadData(100);
          console.log(res);
        })
        .catch((err) => {
          toast.error('Remove Error')
          console.log(err);
        });
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-2">
          <MenubarAdmin />
        </div>

        <div className="col">
          {loading ? <h1>Loading....</h1> : <h1> <span class="badge" style={ {backgroundColor: '#3B82F6', color: 'white', fontSize: '50px', padding: '10px', borderRadius: '10px', margin: '10px', textAlign: 'center', display: 'block'} }>Home ADMIN</span></h1>}

          <div className="row">
            {product.map((item) => (
              <div key={item._id} className="col-md-4 pb-3">
                <AdminProductCard handleRemove={handleRemove} product={item} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
