import React, { useEffect, useState } from 'react'
import { Switch, Select, Tag, Modal } from "antd";
import MenubarAdmin from '../../layouts/MenubarAdmin'
import { useSelector } from 'react-redux'// useSelector ตัวเข้าถึง store
import Form from 'react-bootstrap/Form';
import { DeleteOutlined, EditOutlined } from "@ant-design/icons";



//functions
import { listUser, changeRole, changeStatus } from '../../functions/users'
const ManageAdmin = () => {
    const { user } = useSelector((state) => ({ ...state }))// ตัวเข้าถึง store ของ user
    const [data, setData] = useState([])// เก็บข้อมูล
    console.log(data)




    useEffect(() => {

        //code 
        loadData(user.token)//ส่ง token ให้ loadData เพื่อเรียกใช้งานฟังก์ชัน listUser เพื่อเรียกใช้งานฟังก์ชันจากการทำงานของฟังก์ชัน


    }, [])
    const loadData = (authtoken) => {// ฟังก์ชันสำหรับโหลดข้อมูลจาก api จาก token
        //code
        listUser(authtoken)
            .then(res => {
                //code


                setData(res.data)
            })
            .catch(err => {
                //err

                console.log(err.response.data)
            })

    }

    const handleOnchange = (e, id) => {
        const value = {
            id: id,
            enabled: e,
        };
        changeStatus(user.token, value)
            .then((res) => {
                console.log(res);
                loadData(user.token);// เรียกใช้ฟังก์ชัน loadData เพื่อโหลดข้อมูลจาก api จาก token
            })
            .catch((err) => {
                console.log(err.response);
            });
    };

    const handleChangeRole = (e, id) => {
        let values = {
            id: id,
            role: e,
        };
        changeRole(user.token, values)
            .then((res) => {
                console.log(res);
                loadData(user.token);
            })
            .catch((err) => {
                console.log(err.response);
            });
    };


    const roleData = ["admin", "user"];







    return (
        <div className='container-fluid'>
            <div className='row'>
                <div className='col-md-2'>
                    <MenubarAdmin />
                </div>

                <div className='col-md-10'>
                    <h1>Manage Admin</h1>

                    <table class="table">
                        <thead>
                            <tr>
                                <th scope="col">username</th>
                                <th scope="col">role</th>
                                <th scope="col">status</th>
                                <th scope="col">created</th>
                                <th scope="col">updated</th>
                            </tr>
                        </thead>
                        <tbody class="table-group-divider">
                            {data.map((item, index) => ( // วนลูปแสดงข้อมูลจาก data ที่ได้จาก api
                                <tr>
                                    <th scope="row">{item.username}</th>


                                    <td>
                                        <Select
                                            style={{ width: "100%" }}
                                            value={item.role}
                                            onChange={(e) => handleChangeRole(e, item._id)}
                                        >
                                            {roleData.map((item, index) => (
                                                <Select.Option value={item} key={index}>
                                                    {item == "admin" ? (
                                                        <Tag color="green">{item}</Tag>
                                                    ) : (
                                                        <Tag color="red">{item}</Tag>
                                                    )}
                                                </Select.Option>
                                            ))}
                                        </Select>
                                    </td>
                                    <td>
                                        <Switch
                                            checked={item.enabled}
                                            onChange={(e) => handleOnchange(e, item._id)}
                                        />
                                    </td>
                                    <td>{item.createdAt}</td>
                                    <td>{item.updatedAt}</td>
                                    <td>
                                        <DeleteOutlined  />
                                        <EditOutlined  />
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>

    )
}

export default ManageAdmin
