import React, { useState, useEffect} from 'react'
import MenubarAdmin from '../../../layouts/MenubarAdmin'
import { toast } from 'react-toastify';
import { useSelector } from 'react-redux';


// functions
import { createProduct } from "../../../functions/product";

const initialstate = {
    title: "",
    description: "",
    categories: [],
    category: "",
    price: "",
    quantity: "",
    images: [],
  };

const Home = () => {
    const { user } = useSelector((state) => ({ ...state }));
    const [values, setValues] = useState(initialstate);


   const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
    console.log(values);
   }

   const handleSubmit = (e) => {
    e.preventDefault();
    createProduct(user.token, values)
      .then((res) => {
        console.log(res);
        toast.success("Insert " + res.data.title + " Success!!");
        window.location.reload()
      })
      .catch((err) => {
        console.log(err.response);
        toast.error(err.response.data);
      });
  };






  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='col-md-2'>
          <MenubarAdmin />
          </div>

          <div className='col-md-10'>
            <h1>Create Product Page</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Title</label>
                    <input type="text" className="form-control"  name='title' value={values.title} onChange={handleChange} />{/*placeholder="Enter Title"*/}
                </div>
                <div className="form-group">
                    <label>description</label>
                    <input type="text" className="form-control"  name='description' value={values.description} onChange={handleChange} />
                </div>
             
                <div className="form-group">
                    <label>price</label>
                    <input type="numbar" className="form-control"  name='price' value={values.price} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>quantity</label>
                    <input type="numbar" className="form-control"  name='quantity' value={values.quantity} onChange={handleChange} />
                </div>

                <button className="btn btn-primary">Submit</button>
            </form>
          </div>

          </div>

    </div>
  )
}

export default Home
