import React, {  useState,useEffect } from 'react'
import { useSelector } from 'react-redux'// useSelector ตัวเข้าถึง store 
import LoadingToRedirect from './LoadingToRedirect'
import { currentAdmin } from '../functions/auth'

const AdminRoute = ({children}) => {

  const { user } = useSelector(state => ({...state}));// ตัวเข้าถึง store ของ user
  const [ ok , setOk ] = useState(false)

  useEffect(() => {
    if(user && user.token){
        currentAdmin(user.token)
        .then(res => {
            console.log(res)
        }).catch(err => {
            console.log(err)
        })
    }

  }, [user])

    
  return ok
  ? children
  : <LoadingToRedirect />// ถ้าไม่ใช่ user ให้ไปหน้าที่ LoadingToRedirect
}

export default AdminRoute
