import React, { useState } from 'react'
import { register } from '../../functions/auth';


const Register = () => {
    const [value, setValue] = useState({// เก็บค่าที่รับมาจาก input
        username: "",
        password: "",
        password1: "",
      
    });

    const handleChange = (e) => {
        setValue({...value,[e.target.name]:e.target.value});// เก็บค่าที่รับมาจาก input ไว้ในตัวแปร value
    };



    // ฟังก์ชันสำหรับการส่งข้อมูล
    const handleSubmit = (e) => {
        e.preventDefault();// ไม่ให้หน้าเว็บ refresh
        console.log(value);
        if (value.password !== value.password1) {// ตรวจสอบ password กับ password1 ว่าตรงกันไหม
            alert("password not match");
        } else {
            register(value)
        }
    };

    



  return (
    <div>
        <form onSubmit={handleSubmit}>
            <label>Username</label>
            <input type="text" name='username' onChange={handleChange}/> {/*เมื่อเปลี่ยนค่าให้ทำการเรียกใช้ handleChange*/}

            <label>Password</label>
            <input type="text" name='password' onChange={handleChange}/>
            <br/>
            <label>Confirm Password</label>
            <input type="text" name='password1' onChange={handleChange}/>
           
         


            <button disabled={value.password.length < 6} type='submit'>Register</button> {/*ถ้า password น้อยกว่า 6 ตัว ปุ่มจะไม่สามารถกดได้*/}

        </form>
    </div>
  )
}

export default Register
