export function userReducer() {
  let state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : null;
  let action = arguments.length > 1 ? arguments[1] : undefined;

  // สร้าง Store เพื่อเก็บข้อมูลผู้ใช้งาน
  switch (action.type) {
    // ตรวจสอบว่า action ที่ส่งมาเป็นอะไร
    case "LOGIN":
      return action.payload;
    // ถ้าเป็น LOGIN ให้เก็บข้อมูลผู้ใช้งานไว้ใน Store

    case "LOGOUT":
      localStorage.clear(); // ล้างข้อมูลที่เก็บไว้ใน localStorage

      return action.payload;

    default:
      return state;
    // ค่า null 
  }
}
