//rafce
import React from "react";
import {
  Document,
  Page,
  Text,
  View,
  StyleSheet,
  Font,
} from "@react-pdf/renderer";
import {
  Table,
  TableHeader,
  TableCell,
  TableBody,
  DataTableCell,
} from "@david.kucsai/react-pdf-table";

import fontDev from "./Kanit-Bold.ttf";
import moment from "moment/min/moment-with-locales";

// Register font
Font.register({ family: "Kanit", src: fontDev });

// Create styles
const styles = StyleSheet.create({
  page: {
    flexDirection: "row",
    backgroundColor: "#E4E4E4",
    fontFamily: "Kanit",
    textAlign: "center",

  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,

  },
  summary: {
    textAlign: 'right',

  }
});
const Invoice = ({ order }) => {
  return (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <Text>Kapong Chaipat Hospital</Text>
          <Text>{moment(Date.now()).locale("th").format("LLLL")}</Text>

          <Table>
            <TableHeader>
              <TableCell>รายการสินค้า</TableCell>
              <TableCell>ราคาสินค้า</TableCell>
              <TableCell>จำนวน</TableCell>
            </TableHeader>
          </Table>
          <Table data={order.products}>
            <TableBody>
              <DataTableCell getContent={(x) => x.product.title} />
              <DataTableCell getContent={x => x.price} />
              <DataTableCell getContent={x => x.count} />
            </TableBody>
          </Table>
          <Text style={styles.summary}>ค่าจัดส่ง : 35</Text>
          <Text style={styles.summary}>ราคารวมสุทธิ : {order.cartTotal}</Text>

        </View>
      </Page>
    </Document>
  );
};

export default Invoice;
