import React, { useEffect, useState } from 'react'
import { Switch } from 'antd';
import MenubarAdmin from '../../layouts/MenubarAdmin'
import { useSelector } from 'react-redux'// useSelector ตัวเข้าถึง store
import Form from 'react-bootstrap/Form';
import { Space, Table, Tag } from 'antd';



//functions
import { listUser } from '../../functions/users'
const ManageAdmin = () => {
    // const { user } = useSelector((state) => ({ ...state }))// ตัวเข้าถึง store ของ user
    // const [data, setData] = useState([])// เก็บข้อมูล
    // console.log(data)




    // useEffect(() => {

    //     //code 
    //     loadData(user.token)//ส่ง token ให้ loadData เพื่อเรียกใช้งานฟังก์ชัน listUser เพื่อเรียกใช้งานฟังก์ชันจากการทำงานของฟังก์ชัน


    // }, [])
    // const loadData = (authtoken) => {// ฟังก์ชันสำหรับโหลดข้อมูลจาก api จาก token
    //     //code
    //     listUser(authtoken)
    //         .then(res => {
    //             //code


    //             setData(res.data)
    //         })
    //         .catch(err => {
    //             //err

    //             console.log(err.response.data)
    //         })

    // }
    


    const columns = [
        {
          title: 'Name',
          dataIndex: 'name',
          key: 'name',
          render: (text) => <a>{text}</a>,
        },
        {
          title: 'Age',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'Address',
          dataIndex: 'address',
          key: 'address',
        },
        {
          title: 'Tags',
          key: 'tags',
          dataIndex: 'tags',
          render: (_, { tags }) => (
            <>
              {tags.map((tag) => {
                let color = tag.length > 5 ? 'geekblue' : 'green';
      
                if (tag === 'loser') {
                  color = 'volcano';
                }
      
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          ),
        },
        {
          title: 'Action',
          key: 'action',
          render: (_, record) => (
            <Space size="middle">
              <a>Invite {record.name}</a>
              <a>Delete</a>
            </Space>
          ),
        },
      ];
      const data = [
        {
          key: '1',
          name: 'John Brown',
          age: 32,
          address: 'New York No. 1 Lake Park',
          tags: ['nice', 'developer'],
        },
        {
          key: '2',
          name: 'Jim Green',
          age: 42,
          address: 'London No. 1 Lake Park',
          tags: ['loser'],
        },
        {
          key: '3',
          name: 'Joe Black',
          age: 32,
          address: 'Sidney No. 1 Lake Park',
          tags: ['cool', 'teacher'],
        },
      ];










    return (
        // <div className='container-fluid'>
        //     <div className='row'>
        //         <div className='col-md-2'>
        //             <MenubarAdmin />
        //         </div>

        //         <div className='col-md-10'>
        //             <h1>Manage Admin</h1>

        //             <table class="table">
        //                 <thead>
        //                     <tr>
        //                         <th scope="col">username</th>
        //                         <th scope="col">role</th>
        //                         <th scope="col">status</th>
        //                         <th scope="col">creared</th>
        //                         <th scope="col">updated</th>
        //                     </tr>
        //                 </thead>
        //                 <tbody class="table-group-divider">
        //                     {data.map((item, index) => ( // วนลูปแสดงข้อมูลจาก data ที่ได้จาก api
        //                         <tr>
        //                             <th scope="row">{item.username}</th>
        //                             <td>{item.role} </td>
        //                             <td>
        //                             <Form.Select style={{width:'60%'}}>
        //                                 <option>Large select</option>
        //                             </Form.Select>
        //                             </td>
        //                             <td>
        //                                 <Form.Check checked={item.enabled} type="switch" />
        //                             </td>
        //                             <td>{item.createdAt}</td>
        //                             <td>{item.updatedAt}</td>
        //                         </tr>
        //                     ))}
        //                 </tbody>
        //             </table>
        //         </div>
        //     </div>
        // </div>
        <Table columns={columns} dataSource={data} />

    )
}

export default ManageAdmin
